      </Route>
      <Route exact path="/MyDeck">
        <MyDeck/>
      </Route>
      <Route exact path="/RandomDiceRoll">
        <RandomDiceRoll/>
      </Route>
      <Route exact path="/">
        <Home/>
      </Route>
    </Switch>
    </div>
  );
}

export default App;
