import './App.css';
import React, { useEffect, useState } from "react";
import { Route, Switch } from "react-router-dom";
import Home from './Components/Home';
import NavBar from './Components/NavBar';
import DeckBuilder from './Components/DeckBuilder';
import MyDeck from './Components/MyDeck';


function App() {

  const [input, setInput] = useState('');
  const [mtgCardsList, setMtgCardsList] = useState();

  const fetchData = async (searchInput) => {
  return await fetch(`https://api.magicthegathering.io/v1/cards?name=${searchInput}`)
  .then(res => res.json())
  .then(data => {
      const cardData = data.cards
      cardData.forEach((card, i) => {
          if (!card.imageUrl){
              cardData.splice(i)  
          }
          setMtgCardsList(cardData)
      })
  })
  }

  const updateInput = async (input) => {
      setInput(input);
      await fetchData(input);
  }
  console.log(mtg)
  useEffect( () => {fetchData("")}, []);
  return (
    <div >
      <NavBar/>
      <Switch>
      <Route exact path="/DeckBuilder">
        <DeckBuilder input={input} onChange={updateInput} mtgCardsList={mtgCardsList}/>
      </Route>
      <Route exact path="/MyDeck">
        <MyDeck/>
      </Route>
      <Route exact path="/">
        <Home/>
      </Route>
    </Switch>
    </div>
  );
}

export default App;
