import React, { useState } from "react";
import MtgCard from "./MtgCard";

function CardList({ mtgCardsList = [] }) {

    const [mtgMyDeck, setMtgMyDeck] = useState([])
    const [deckCardNumber, setDeckCardNumber] = useState(0)
    const cardsWithImages = mtgCardsList.map(card => card.imageUrl == true)

    function handleOnClick(card) { 
        setMtgMyDeck(mtgMyDeck.concat(card.imageUrl))
        setDeckCardNumber(deckCardNumber + 1)
        console.log(mtgMyDeck)
    }

    return (
        <div>
            <button>Cards in Deck :{deckCardNumber}</button>
            <div className="ui three stackable cards">

                {mtgCardsList.map((card) => (
                    <MtgCard
                        key={card.id}
                        card={card}
                        handleOnClick={handleOnClick}
                    />
                ))}
            </div>
        </div>
    )
}

export default CardList
