import React, { useEffect, useState } from "react";
import CardList from "./CardList";
import SearchBar from "./SearchBar";

function DeckBuilder() {
const [input, setInput] = useState('');
const [mtgCardsList, setMtgCardsList] = useState();
const [mtgCardsListDefault, setMtgCardsListDefault] = useState();

const fetchData = async () => {
return await fetch("https://api.magicthegathering.io/v1/cards")
.then(res => res.json())
.then(data => {
    setMtgCardsList(data.cards)
    setMtgCardsListDefault(data.cards)
})
}

const updateInput = async (input) => {
    console.log()
    const filtered = mtgCardsListDefault.filtered(mtgCard => {
        return mtgCard.className.toLowerCare().includes(input.toLowerCase())
    })
    setInput(input);
    setMtgCardsList(filtered)
}

useEffect( () => {fetchData()}, []);

    return( <div>  
              {mtgCardsList && <SearchBar input={input} onChange={updateInput}/>} 
                    <div className="cardcontainer">
                {mtgCardsList &&<CardList mtgCardsList={mtgCardsList}/>}
                    </div>
            </div>
    )
}

export default DeckBuilder;
