import React, { useState } from "react";
import MtgCard from "./MtgCard";

function CardList({mtgCardsList=[]}) {

const [mtgMyDeck, setMtgMyDeck] = useState([])
const [deckCardNumber, setDeckCardNumber] = useState(1)
function handleOnClick(e){
setMtgMyDeck(e.target)
setDeckCardNumber(deckCardNumber + 1)
console.log(deckCardNumber)
}

    return( 
    <div>
        <button>Cards in Deck :{deckCardNumber}</button>
        <div className="ui three stackable cards">

            { mtgCardsList.map((mtgCardList) => (
                <MtgCard
                key={mtgCardList.id}
                cards={mtgCardList}
                handleOnClick={handleOnClick}
                />
            ))}
        </div>
    </div>
    )}

export default CardList
