import React, { useEffect, useState } from "react";
import CardList from "./CardList";
import SearchBar from "./SearchBar";
import cardback from "./"
function DeckBuilder() {
const [input, setInput] = useState('');
const [mtgCardsList, setMtgCardsList] = useState();


const fetchData = async (searchInput) => {
return await fetch(`https://api.magicthegathering.io/v1/cards?name=${searchInput}`)
.then(res => res.json())
.then(data => {
    data.cards.forEach((card) => {
        if (card.imageUrl === undefined){
            card.imageUrl = "./images/cardback.png"
        }
    })
    setMtgCardsList(data.cards)

})
}

const updateInput = async (input) => {
    setInput(input);
    await fetchData(input);
}

useEffect( () => {fetchData("")}, []);

    return( <div>  
              {mtgCardsList && <SearchBar input={input} onChange={updateInput}/>} 
                    <div className="cardcontainer">
                {mtgCardsList && <CardList mtgCardsList={mtgCardsList}/>}
                    </div>
            </div>
    )
}

export default DeckBuilder;
